import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as a,c as e,f as r}from"./app.0cea91aa.js";const i={},t=r('<p>\u53C2\u8003\uFF1Ahttps://www.zhihu.com/question/54152397?sort=created</p><h2 id="_1-\u672C\u8D28" tabindex="-1"><a class="header-anchor" href="#_1-\u672C\u8D28" aria-hidden="true">#</a> 1.\u672C\u8D28</h2><p>\u5C06 MQ \u63B0\u5F00\u4E86\u63C9\u788E\u4E86\u6765\u770B\uFF0C\u90FD\u662F\u300C\u4E00\u53D1\u4E00\u5B58\u4E00\u6D88\u8D39\u300D\uFF0C\u518D\u76F4\u767D\u70B9\u5C31\u662F\u4E00\u4E2A\u300C\u8F6C\u53D1\u5668\u300D\u3002</p><p>\u751F\u4EA7\u8005\u5148\u5C06\u6D88\u606F\u6295\u9012\u4E00\u4E2A\u53EB\u505A\u300C\u961F\u5217\u300D\u7684\u5BB9\u5668\u4E2D\uFF0C\u7136\u540E\u518D\u4ECE\u8FD9\u4E2A\u5BB9\u5668\u4E2D\u53D6\u51FA\u6D88\u606F\uFF0C\u6700\u540E\u518D\u8F6C\u53D1\u7ED9\u6D88\u8D39\u8005\uFF0C\u4EC5\u6B64\u800C\u5DF2\u3002</p><p><img src="https://i.loli.net/2021/10/20/SKUJXckV7d1mpbT.jpg" alt="img"></p><h2 id="_2-\u6A21\u578B\u6F14\u8FDB" tabindex="-1"><a class="header-anchor" href="#_2-\u6A21\u578B\u6F14\u8FDB" aria-hidden="true">#</a> 2.\u6A21\u578B\u6F14\u8FDB</h2><h4 id="\u57FA\u7840\u961F\u5217\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#\u57FA\u7840\u961F\u5217\u6A21\u578B" aria-hidden="true">#</a> \u57FA\u7840\u961F\u5217\u6A21\u578B</h4><p>\u662F\u4E00\u4E2A\u4E25\u683C\u610F\u4E49\u4E0A\u7684\u961F\u5217\uFF08Queue\uFF09\u3002\u6D88\u606F\u6309\u7167\u4EC0\u4E48\u987A\u5E8F\u5199\u8FDB\u53BB\uFF0C\u5C31\u6309\u7167\u4EC0\u4E48\u987A\u5E8F\u8BFB\u51FA\u6765\u3002</p><p>\u5141\u8BB8\u591A\u4E2A\u751F\u4EA7\u8005\u5F80\u540C\u4E00\u4E2A\u961F\u5217\u53D1\u9001\u6D88\u606F\u3002\u4F46\u662F\uFF0C\u5982\u679C\u6709\u591A\u4E2A\u6D88\u8D39\u8005\uFF0C\u5B9E\u9645\u4E0A\u662F\u7ADE\u4E89\u7684\u5173\u7CFB\uFF0C\u4E5F\u5C31\u662F\u4E00\u6761\u6D88\u606F\u53EA\u80FD\u88AB\u5176\u4E2D\u4E00\u4E2A\u6D88\u8D39\u8005\u63A5\u6536\u5230\uFF0C\u8BFB\u5B8C\u5373\u88AB\u5220\u9664\u3002</p><h4 id="" tabindex="-1"><a class="header-anchor" href="#" aria-hidden="true">#</a> <img src="https://i.loli.net/2021/10/20/bRMlYVQwaXT6m9y.jpg" alt="img"></h4><h4 id="\u53D1\u5E03\u8BA2\u9605\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#\u53D1\u5E03\u8BA2\u9605\u6A21\u578B" aria-hidden="true">#</a> \u53D1\u5E03\u8BA2\u9605\u6A21\u578B</h4><p>\u5982\u679C\u9700\u8981\u5C06\u4E00\u4EFD\u6D88\u606F\u6570\u636E\u5206\u53D1\u7ED9\u591A\u4E2A\u6D88\u8D39\u8005\uFF0C\u5E76\u4E14\u6BCF\u4E2A\u6D88\u8D39\u8005\u90FD\u8981\u6C42\u6536\u5230\u5168\u91CF\u7684\u6D88\u606F\u3002\u5F88\u663E\u7136\uFF0C\u961F\u5217\u6A21\u578B\u65E0\u6CD5\u6EE1\u8DB3\u8FD9\u4E2A\u9700\u6C42\u3002</p><p>\u4E00\u4E2A\u53EF\u884C\u7684\u65B9\u6848\u662F\uFF1A\u4E3A\u6BCF\u4E2A\u6D88\u8D39\u8005\u521B\u5EFA\u4E00\u4E2A\u5355\u72EC\u7684\u961F\u5217\uFF0C\u8BA9\u751F\u4EA7\u8005\u53D1\u9001\u591A\u4EFD\u3002\u8FD9\u79CD\u505A\u6CD5\u6BD4\u8F83\u7B28\uFF0C\u800C\u4E14\u540C\u4E00\u4EFD\u6570\u636E\u4F1A\u88AB\u590D\u5236\u591A\u4EFD\uFF0C\u4E5F\u5F88\u6D6A\u8D39\u7A7A\u95F4\u3002</p><p>\u4E3A\u4E86\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\uFF0C\u5C31\u6F14\u5316\u51FA\u4E86\u53E6\u5916\u4E00\u79CD\u6D88\u606F\u6A21\u578B\uFF1A\u53D1\u5E03-\u8BA2\u9605\u6A21\u578B\u3002</p><p><img src="https://i.loli.net/2021/10/20/gCszyAjRu7dX8bw.jpg" alt="img"></p><h4 id="\u5C0F\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u5C0F\u7ED3" aria-hidden="true">#</a> \u5C0F\u7ED3</h4><p>\u7B80\u5355\u800C\u8A00\u5C31\u662F\u5355\u64AD\u548C\u5E7F\u64AD\u7684\u533A\u522B\u3002\u961F\u5217\u6A21\u578B\u53EA\u4F1A\u53D1\u7ED9\u4E00\u4E2A\u961F\u5217\uFF0C\u6709\u4E00\u4E2A\u751F\u4EA7\u8005\u548C\u6D88\u8D39\u8005\u3002\u53D1\u5E03\u8BA2\u9605\u6A21\u578B\uFF0C\u4E00\u4E2A\u751F\u4EA7\u8005\u53EF\u4EE5\u6709\u591A\u4E2A\u6D88\u8D39\u8005\u3002\u5F53\u53D1\u5E03-\u8BA2\u9605\u6A21\u578B\u4E2D\u53EA\u6709 1 \u4E2A\u8BA2\u9605\u8005\u65F6\uFF0C\u5B83\u548C\u961F\u5217\u6A21\u578B\u5C31\u4E00\u6837\u4E86\uFF0C\u56E0\u6B64\u5728\u529F\u80FD\u4E0A\u662F\u5B8C\u5168\u517C\u5BB9\u961F\u5217\u6A21\u578B\u7684\u3002</p><p>\u8FD9\u4E5F\u89E3\u91CA\u4E86\u4E3A\u4EC0\u4E48\u73B0\u4EE3\u4E3B\u6D41\u7684 RocketMQ\u3001Kafka \u90FD\u662F\u76F4\u63A5\u57FA\u4E8E\u53D1\u5E03-\u8BA2\u9605\u6A21\u578B\u5B9E\u73B0\u7684\uFF1F\u6B64\u5916\uFF0CRabbitMQ \u4E2D\u4E4B\u6240\u4EE5\u6709\u4E00\u4E2A Exchange \u6A21\u5757\uFF1F\u5176\u5B9E\u4E5F\u662F\u4E3A\u4E86\u89E3\u51B3\u6D88\u606F\u7684\u6295\u9012\u95EE\u9898\uFF0C\u53EF\u4EE5\u53D8\u76F8\u5B9E\u73B0\u53D1\u5E03-\u8BA2\u9605\u6A21\u578B\u3002</p><p>\u6D88\u606F\u6A21\u578B\uFF0C\u5B9E\u9645\u89E3\u51B3\u7684\u662F\u751F\u4EA7\u8005\u548C\u6D88\u8D39\u8005\u7684\u901A\u4FE1\u95EE\u9898\u3002\u5BF9\u6BD4 RPC \uFF0C\u5F88\u660E\u663E\u7684\u53D8\u5316\u5728\u4E8E\u4E00\u6B21 RPC \u53D8\u6210\u4E86\u73B0\u5728\u7684\u4E24\u6B21 RPC\uFF0C\u5C06\u540C\u6B65\u53D8\u6210\u4E86\u5F02\u6B65\u3002</p><p><img src="https://i.loli.net/2021/10/20/itfIbvSNLgrW68j.jpg" alt="img"></p><h2 id="_3-\u5E94\u7528\u573A\u666F" tabindex="-1"><a class="header-anchor" href="#_3-\u5E94\u7528\u573A\u666F" aria-hidden="true">#</a> 3.\u5E94\u7528\u573A\u666F</h2><h4 id="\u5E38\u89C1\u7684\u5E94\u7528\u573A\u666F" tabindex="-1"><a class="header-anchor" href="#\u5E38\u89C1\u7684\u5E94\u7528\u573A\u666F" aria-hidden="true">#</a> \u5E38\u89C1\u7684\u5E94\u7528\u573A\u666F:</h4><p>\u7CFB\u7EDF\u89E3\u8026\uFF0C</p><p>\u5F02\u6B65\u901A\u4FE1\uFF0C</p><p>\u6D41\u91CF\u524A\u5CF0\uFF0C</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u8FD8\u6709\u5EF6\u8FDF\u901A\u77E5\u3001\u6700\u7EC8\u4E00\u81F4\u6027\u4FDD\u8BC1\u3001\u987A\u5E8F\u6D88\u606F\u3001\u6D41\u5F0F\u5904\u7406\u7B49\u7B49\u3002</p><p>\u5148\u6709\u4E86\u5E94\u7528\u573A\u666F\uFF08\u95EE\u9898\uFF09\uFF0C\u7136\u540E\u624D\u6709\u4E86\u6D88\u606F\u6A21\u578B\uFF0C\u56E0\u4E3A\u6D88\u606F\u6A21\u578B\u662F\u89E3\u51B3\u65B9\u6848\u7684\u62BD\u8C61\u3002</p><h4 id="\u7279\u6027\u4E0E\u573A\u666F" tabindex="-1"><a class="header-anchor" href="#\u7279\u6027\u4E0E\u573A\u666F" aria-hidden="true">#</a> \u7279\u6027\u4E0E\u573A\u666F:</h4><p>\u4E3B\u8981\u7279\u6027\u5C31\u662F\u5F02\u6B65\uFF08\u8BF7\u6C42\u62C6\u5206\uFF09\u548C\u89E3\u8026\uFF08\u529F\u80FD\uFF09\u3002</p><p>\u4E3E\u4E00\u4E2A\u5B9E\u9645\u4F8B\u5B50\uFF0C\u6BD4\u5982\u8BF4\u7535\u5546\u4E1A\u52A1\u4E2D\u6700\u5E38\u89C1\u7684\u300C\u8BA2\u5355\u652F\u4ED8\u300D\u573A\u666F\uFF1A\u5728\u8BA2\u5355\u652F\u4ED8\u6210\u529F\u540E\uFF0C\u9700\u8981\u66F4\u65B0\u8BA2\u5355\u72B6\u6001\u3001\u66F4\u65B0\u7528\u6237\u79EF\u5206\u3001\u901A\u77E5\u5546\u5BB6\u6709\u65B0\u8BA2\u5355\u3001\u66F4\u65B0\u63A8\u8350\u7CFB\u7EDF\u4E2D\u7684\u7528\u6237\u753B\u50CF\u7B49\u7B49\u3002</p><p><img src="https://i.loli.net/2021/10/20/NYE78bvPM4oIB2u.jpg" alt="img"></p><p>\u5F15\u5165 MQ \u540E\uFF0C\u8BA2\u5355\u652F\u4ED8\u73B0\u5728\u53EA\u9700\u8981\u5173\u6CE8\u5B83\u6700\u91CD\u8981\u7684\u6D41\u7A0B\uFF1A\u66F4\u65B0\u8BA2\u5355\u72B6\u6001\u5373\u53EF\u3002\u5176\u4ED6\u4E0D\u91CD\u8981\u7684\u4E8B\u60C5\u5168\u90E8\u4EA4\u7ED9 MQ \u6765\u901A\u77E5\u3002\u8FD9\u4FBF\u662F MQ \u89E3\u51B3\u7684\u6700\u6838\u5FC3\u7684\u95EE\u9898\uFF1A\u7CFB\u7EDF\u89E3\u8026\u3002\u6539\u9020\u524D\u8BA2\u5355\u7CFB\u7EDF\u4F9D\u8D56 3 \u4E2A\u5916\u90E8\u7CFB\u7EDF\uFF0C\u6539\u9020\u540E\u4EC5\u4EC5\u4F9D\u8D56 MQ\uFF0C\u800C\u4E14\u540E\u7EED\u4E1A\u52A1\u518D\u6269\u5C55\uFF08\u6BD4\u5982\uFF1A\u8425\u9500\u7CFB\u7EDF\u6253\u7B97\u9488\u5BF9\u652F\u4ED8\u7528\u6237\u5956\u52B1\u4F18\u60E0\u5238\uFF09\uFF0C\u4E5F\u4E0D\u6D89\u53CA\u8BA2\u5355\u7CFB\u7EDF\u7684\u4FEE\u6539\uFF0C\u4ECE\u800C\u4FDD\u8BC1\u4E86\u6838\u5FC3\u6D41\u7A0B\u7684\u7A33\u5B9A\u6027\uFF0C\u964D\u4F4E\u4E86\u7EF4\u62A4\u6210\u672C\u3002</p><p>\u8FD9\u4E2A\u6539\u9020\u8FD8\u5E26\u6765\u4E86\u53E6\u5916\u4E00\u4E2A\u597D\u5904\uFF1A\u56E0\u4E3A MQ \u7684\u5F15\u5165\uFF0C\u66F4\u65B0\u7528\u6237\u79EF\u5206\u3001\u901A\u77E5\u5546\u5BB6\u3001\u66F4\u65B0\u7528\u6237\u753B\u50CF\u8FD9\u4E9B\u6B65\u9AA4\u5168\u90E8\u53D8\u6210\u4E86\u5F02\u6B65\u6267\u884C\uFF0C\u80FD\u51CF\u5C11\u8BA2\u5355\u652F\u4ED8\u7684\u6574\u4F53\u8017\u65F6\uFF0C\u63D0\u5347\u8BA2\u5355\u7CFB\u7EDF\u7684\u541E\u5410\u91CF\u3002\u8FD9\u4FBF\u662F MQ \u7684\u53E6\u4E00\u4E2A\u5178\u578B\u5E94\u7528\u573A\u666F\uFF1A</p><p>\u5F02\u6B65\u901A\u4FE1\u3002</p><h2 id="_4-\u5982\u4F55\u8BBE\u8BA1\u4E00\u4E2A-mq" tabindex="-1"><a class="header-anchor" href="#_4-\u5982\u4F55\u8BBE\u8BA1\u4E00\u4E2A-mq" aria-hidden="true">#</a> 4.\u5982\u4F55\u8BBE\u8BA1\u4E00\u4E2A MQ\uFF1F</h2><h3 id="\u96CF\u5F62" tabindex="-1"><a class="header-anchor" href="#\u96CF\u5F62" aria-hidden="true">#</a> - \u96CF\u5F62</h3><p>\u7B80\u5355\u800C\u8A00\uFF0C\u4E00\u53D1\u4E00\u5B58\u4E00\u6D88\u8D39\uFF0C\u5C31\u662F MQ \u6700\u6838\u5FC3\u7684\u529F\u80FD\u9700\u6C42\u3002\u53E6\u5916\uFF0C\u4ECE\u6280\u672F\u7EF4\u5EA6\u6765\u770B MQ \u7684\u901A\u4FE1\u6A21\u578B\uFF0C\u53EF\u4EE5\u7406\u89E3\u6210\uFF1A\u4E24\u6B21 RPC + \u6D88\u606F\u8F6C\u50A8\u3002</p><p>\u6709\u4E86\u8FD9\u4E9B\u7406\u89E3\uFF0C\u53EA\u8981\u6709\u4E00\u5B9A\u7684\u7F16\u7A0B\u57FA\u7840\uFF0C\u4E0D\u7528 1 \u4E2A\u5C0F\u65F6\u5C31\u80FD\u5199\u51FA\u4E00\u4E2A MQ \u96CF\u5F62\uFF1A</p><p>1\u3001\u76F4\u63A5\u5229\u7528\u6210\u719F\u7684 RPC \u6846\u67B6\uFF08Dubbo \u6216\u8005 Thrift\uFF09\uFF0C\u5B9E\u73B0\u4E24\u4E2A\u63A5\u53E3\uFF1A\u53D1\u6D88\u606F\u548C\u8BFB\u6D88\u606F\u3002</p><p>2\u3001\u6D88\u606F\u653E\u5728\u672C\u5730\u5185\u5B58\u4E2D\u5373\u53EF\uFF0C\u6570\u636E\u7ED3\u6784\u53EF\u4EE5\u7528 JDK \u81EA\u5E26\u7684 ArrayBlockingQueue \u3002</p><h3 id="\u751F\u4EA7\u73AF\u5883-\u5B9E\u9645\u573A\u666F\u601D\u8003" tabindex="-1"><a class="header-anchor" href="#\u751F\u4EA7\u73AF\u5883-\u5B9E\u9645\u573A\u666F\u601D\u8003" aria-hidden="true">#</a> - \u751F\u4EA7\u73AF\u5883----\u5B9E\u9645\u573A\u666F\u601D\u8003</h3><ul><li>\u95EE\u9898\u70B9</li></ul><p>\u57FA\u7840\uFF1A\u53D1\u6D88\u606F\u3001\u5B58\u6D88\u606F\u3001\u6D88\u8D39\u6D88\u606F\uFF08\u652F\u6301\u53D1\u5E03-\u8BA2\u9605\u6A21\u5F0F\uFF09\u3002</p><p>\u9AD8\u5E76\u53D1\uFF1A</p><p>1\u3001\u9AD8\u5E76\u53D1\u573A\u666F\u4E0B\uFF0C\u5982\u4F55\u4FDD\u8BC1\u6536\u53D1\u6D88\u606F\u7684\u6027\u80FD\uFF1F</p><p>2\u3001\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u670D\u52A1\u7684\u9AD8\u53EF\u7528\u548C\u9AD8\u53EF\u9760\uFF1F</p><p>3\u3001\u5982\u4F55\u4FDD\u8BC1\u670D\u52A1\u662F\u53EF\u4EE5\u6C34\u5E73\u4EFB\u610F\u6269\u5C55\u7684\uFF1F</p><p>4\u3001\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u5B58\u50A8\u4E5F\u662F\u6C34\u5E73\u53EF\u6269\u5C55\u7684\uFF1F</p><p>5\u3001\u5404\u79CD\u5143\u6570\u636E\uFF08\u6BD4\u5982\u96C6\u7FA4\u4E2D\u7684\u5404\u4E2A\u8282\u70B9\u3001\u4E3B\u9898\u3001\u6D88\u8D39\u5173\u7CFB\u7B49\uFF09\u5982\u4F55\u7BA1\u7406\uFF0C\u9700\u4E0D\u9700\u8981\u8003\u8651\u6570\u636E\u7684\u4E00\u81F4\u6027\uFF1F</p><ul><li>\u6574\u4F53\u601D\u8DEF</li></ul><p>\u5148\u6765\u770B\u4E0B\u6574\u4F53\u67B6\u6784\uFF0C\u4F1A\u6D89\u53CA\u4E09\u7C7B\u89D2\u8272\uFF1A</p><p><img src="https://i.loli.net/2021/10/20/zO6HgTbjMsB9IG5.jpg" alt="img"></p><p>\u53E6\u5916\uFF0C\u5C06\u300C\u4E00\u53D1\u4E00\u5B58\u4E00\u6D88\u8D39\u300D\u8FD9\u4E2A\u6838\u5FC3\u6D41\u7A0B\u8FDB\u4E00\u6B65\u7EC6\u5316\u540E\uFF0C\u6BD4\u8F83\u5B8C\u6574\u7684\u6570\u636E\u6D41\u5982\u4E0B\uFF1A</p><p><img src="https://i.loli.net/2021/10/20/yWsj2S9M6EGJbKL.jpg" alt="img"></p><p>\u57FA\u4E8E\u4E0A\u9762\u4E24\u4E2A\u56FE\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5F88\u5FEB\u660E\u786E\u51FA 3 \u7C7B\u89D2\u8272\u7684\u4F5C\u7528\uFF0C\u5206\u522B\u5982\u4E0B\uFF1A</p><p>1\u3001Broker\uFF08\u670D\u52A1\u7AEF\uFF09\uFF1AMQ \u4E2D\u6700\u6838\u5FC3\u7684\u90E8\u5206\uFF0C\u662F MQ \u7684\u670D\u52A1\u7AEF\uFF0C\u6838\u5FC3\u903B\u8F91\u51E0\u4E4E\u5168\u5728\u8FD9\u91CC\uFF0C\u5B83\u4E3A\u751F\u4EA7\u8005\u548C\u6D88\u8D39\u8005\u63D0\u4F9B RPC \u63A5\u53E3\uFF0C\u8D1F\u8D23\u6D88\u606F\u7684\u5B58\u50A8\u3001\u5907\u4EFD\u548C\u5220\u9664\uFF0C\u4EE5\u53CA\u6D88\u8D39\u5173\u7CFB\u7684\u7EF4\u62A4\u7B49\u3002</p><p>2\u3001Producer\uFF08\u751F\u4EA7\u8005\uFF09\uFF1AMQ \u7684\u5BA2\u6237\u7AEF\u4E4B\u4E00\uFF0C\u8C03\u7528 Broker \u63D0\u4F9B\u7684 RPC \u63A5\u53E3\u53D1\u9001\u6D88\u606F\u3002</p><p>3\u3001Consumer\uFF08\u6D88\u8D39\u8005\uFF09\uFF1AMQ \u7684\u53E6\u5916\u4E00\u4E2A\u5BA2\u6237\u7AEF\uFF0C\u8C03\u7528 Broker \u63D0\u4F9B\u7684 RPC \u63A5\u53E3\u63A5\u6536\u6D88\u606F\uFF0C\u540C\u65F6\u5B8C\u6210\u6D88\u8D39\u786E\u8BA4\u3002</p><h3 id="\u8BE6\u7EC6\u8BBE\u8BA1" tabindex="-1"><a class="header-anchor" href="#\u8BE6\u7EC6\u8BBE\u8BA1" aria-hidden="true">#</a> - \u8BE6\u7EC6\u8BBE\u8BA1</h3><p>\u4E0B\u9762\uFF0C\u518D\u5C55\u5F00\u8BA8\u8BBA\u4E0B\u4E00\u4E9B\u5177\u4F53\u7684\u6280\u672F\u96BE\u70B9\u548C\u53EF\u884C\u7684\u89E3\u51B3\u65B9\u6848\u3002</p><p><strong>\u96BE\u70B91\uFF1ARPC \u901A\u4FE1</strong></p><p>\u89E3\u51B3\u7684\u662F Broker \u4E0E Producer \u4EE5\u53CA Consumer \u4E4B\u95F4\u7684\u901A\u4FE1\u95EE\u9898\u3002\u5982\u679C\u4E0D\u91CD\u590D\u9020\u8F6E\u5B50\uFF0C\u76F4\u63A5\u5229\u7528\u6210\u719F\u7684 RPC \u6846\u67B6 Dubbo \u6216\u8005 Thrift \u5B9E\u73B0\u5373\u53EF\uFF0C\u8FD9\u6837\u4E0D\u9700\u8981\u8003\u8651\u670D\u52A1\u6CE8\u518C\u4E0E\u53D1\u73B0\u3001\u8D1F\u8F7D\u5747\u8861\u3001\u901A\u4FE1\u534F\u8BAE\u3001\u5E8F\u5217\u5316\u65B9\u5F0F\u7B49\u4E00\u7CFB\u5217\u95EE\u9898\u4E86\u3002</p><p>\u5F53\u7136\uFF0C\u4F60\u4E5F\u53EF\u4EE5\u57FA\u4E8E Netty \u6765\u505A\u5E95\u5C42\u901A\u4FE1\uFF0C\u7528 Zookeeper\u3001Euraka \u7B49\u6765\u505A\u6CE8\u518C\u4E2D\u5FC3\uFF0C\u7136\u540E\u81EA\u5B9A\u4E49\u4E00\u5957\u65B0\u7684\u901A\u4FE1\u534F\u8BAE\uFF08\u7C7B\u4F3C Kafka\uFF09\uFF0C\u4E5F\u53EF\u4EE5\u57FA\u4E8E AMQP \u8FD9\u79CD\u6807\u51C6\u5316\u7684 MQ \u534F\u8BAE\u6765\u505A\u5B9E\u73B0\uFF08\u7C7B\u4F3C RabbitMQ\uFF09\u3002\u5BF9\u6BD4\u76F4\u63A5\u7528 RPC \u6846\u67B6\uFF0C\u8FD9\u79CD\u65B9\u6848\u7684\u5B9A\u5236\u5316\u80FD\u529B\u548C\u4F18\u5316\u7A7A\u95F4\u66F4\u5927\u3002</p><p><strong>\u96BE\u70B92\uFF1A\u9AD8\u53EF\u7528\u8BBE\u8BA1</strong></p><p>\u9AD8\u53EF\u7528\u4E3B\u8981\u6D89\u53CA\u4E24\u65B9\u9762\uFF1ABroker \u670D\u52A1\u7684\u9AD8\u53EF\u7528\u3001\u5B58\u50A8\u65B9\u6848\u7684\u9AD8\u53EF\u7528\u3002\u53EF\u4EE5\u62C6\u5F00\u8BA8\u8BBA\u3002</p><p>Broker \u670D\u52A1\u7684\u9AD8\u53EF\u7528\uFF0C\u53EA\u9700\u8981\u4FDD\u8BC1 Broker \u53EF\u6C34\u5E73\u6269\u5C55\u8FDB\u884C\u96C6\u7FA4\u90E8\u7F72\u5373\u53EF\uFF0C\u8FDB\u4E00\u6B65\u901A\u8FC7\u670D\u52A1\u81EA\u52A8\u6CE8\u518C\u4E0E\u53D1\u73B0\u3001\u8D1F\u8F7D\u5747\u8861\u3001\u8D85\u65F6\u91CD\u8BD5\u673A\u5236\u3001\u53D1\u9001\u548C\u6D88\u8D39\u6D88\u606F\u65F6\u7684 ack \u673A\u5236\u6765\u4FDD\u8BC1\u3002</p><p>\u5B58\u50A8\u65B9\u6848\u7684\u9AD8\u53EF\u7528\u6709\u4E24\u4E2A\u601D\u8DEF\uFF1A1\uFF09\u53C2\u8003 Kafka \u7684\u5206\u533A + \u591A\u526F\u672C\u6A21\u5F0F\uFF0C\u4F46\u662F\u9700\u8981\u8003\u8651\u5206\u5E03\u5F0F\u573A\u666F\u4E0B\u6570\u636E\u590D\u5236\u548C\u4E00\u81F4\u6027\u65B9\u6848\uFF08\u7C7B\u4F3C Zab\u3001Raft\u7B49\u534F\u8BAE\uFF09\uFF0C\u5E76\u5B9E\u73B0\u81EA\u52A8\u6545\u969C\u8F6C\u79FB\uFF1B2\uFF09\u8FD8\u53EF\u4EE5\u7528\u4E3B\u6D41\u7684 DB\u3001\u5206\u5E03\u5F0F\u6587\u4EF6\u7CFB\u7EDF\u3001\u5E26\u6301\u4E45\u5316\u80FD\u529B\u7684 KV \u7CFB\u7EDF\uFF0C\u5B83\u4EEC\u90FD\u6709\u81EA\u5DF1\u7684\u9AD8\u53EF\u7528\u65B9\u6848\u3002</p><p><strong>\u96BE\u70B93\uFF1A\u5B58\u50A8\u8BBE\u8BA1</strong></p><p>\u6D88\u606F\u7684\u5B58\u50A8\u65B9\u6848\u662F MQ \u7684\u6838\u5FC3\u90E8\u5206\uFF0C\u53EF\u9760\u6027\u4FDD\u8BC1\u5DF2\u7ECF\u5728\u9AD8\u53EF\u7528\u8BBE\u8BA1\u4E2D\u8C08\u8FC7\u4E86\uFF0C\u53EF\u9760\u6027\u8981\u6C42\u4E0D\u9AD8\u7684\u8BDD\u76F4\u63A5\u7528\u5185\u5B58\u6216\u8005\u5206\u5E03\u5F0F\u7F13\u5B58\u4E5F\u53EF\u4EE5\u3002\u8FD9\u91CC\u91CD\u70B9\u8BF4\u4E00\u4E0B\u5B58\u50A8\u7684\u9AD8\u6027\u80FD\u5982\u4F55\u4FDD\u8BC1\uFF1F\u8FD9\u4E2A\u95EE\u9898\u7684\u51B3\u5B9A\u56E0\u7D20\u5728\u4E8E\u5B58\u50A8\u7ED3\u6784\u7684\u8BBE\u8BA1\u3002</p><p>\u76EE\u524D\u4E3B\u6D41\u7684\u65B9\u6848\u662F\uFF1A\u8FFD\u52A0\u5199\u65E5\u5FD7\u6587\u4EF6\uFF08\u6570\u636E\u90E8\u5206\uFF09 + \u7D22\u5F15\u6587\u4EF6\u7684\u65B9\u5F0F\uFF08\u5F88\u591A\u4E3B\u6D41\u7684\u5F00\u6E90 MQ \u90FD\u662F\u8FD9\u79CD\u65B9\u5F0F\uFF09\uFF0C\u7D22\u5F15\u8BBE\u8BA1\u4E0A\u53EF\u4EE5\u8003\u8651\u7A20\u5BC6\u7D22\u5F15\u6216\u8005\u7A00\u758F\u7D22\u5F15\uFF0C\u67E5\u627E\u6D88\u606F\u53EF\u4EE5\u5229\u7528\u8DF3\u8F6C\u8868\u3001\u4E8C\u4EFD\u67E5\u627E\u7B49\uFF0C\u8FD8\u53EF\u4EE5\u901A\u8FC7\u64CD\u4F5C\u7CFB\u7EDF\u7684\u9875\u7F13\u5B58\u3001\u96F6\u62F7\u8D1D\u7B49\u6280\u672F\u6765\u63D0\u5347\u78C1\u76D8\u6587\u4EF6\u7684\u8BFB\u5199\u6027\u80FD\u3002</p><p>\u5982\u679C\u4E0D\u8FFD\u6C42\u5F88\u9AD8\u7684\u6027\u80FD\uFF0C\u4E5F\u53EF\u4EE5\u8003\u8651\u73B0\u6210\u7684\u5206\u5E03\u5F0F\u6587\u4EF6\u7CFB\u7EDF\u3001KV \u5B58\u50A8\u6216\u8005\u6570\u636E\u5E93\u65B9\u6848\u3002</p><p><strong>\u96BE\u70B94\uFF1A\u6D88\u8D39\u5173\u7CFB\u7BA1\u7406</strong></p><p>\u4E3A\u4E86\u652F\u6301\u53D1\u5E03-\u8BA2\u9605\u7684\u5E7F\u64AD\u6A21\u5F0F\uFF0CBroker \u9700\u8981\u77E5\u9053\u6BCF\u4E2A\u4E3B\u9898\u90FD\u6709\u54EA\u4E9B Consumer \u8BA2\u9605\u4E86\uFF0C\u57FA\u4E8E\u8FD9\u4E2A\u5173\u7CFB\u8FDB\u884C\u6D88\u606F\u6295\u9012\u3002</p><p>\u7531\u4E8E Broker \u662F\u96C6\u7FA4\u90E8\u7F72\u7684\uFF0C\u6240\u4EE5\u6D88\u8D39\u5173\u7CFB\u901A\u5E38\u7EF4\u62A4\u5728\u516C\u5171\u5B58\u50A8\u4E0A\uFF0C\u53EF\u4EE5\u57FA\u4E8E Zookeeper\u3001Apollo \u7B49\u914D\u7F6E\u4E2D\u5FC3\u6765\u7BA1\u7406\u4EE5\u53CA\u8FDB\u884C\u53D8\u66F4\u901A\u77E5\u3002</p><p><strong>\u96BE\u70B95\uFF1A\u9AD8\u6027\u80FD\u8BBE\u8BA1</strong></p><p>\u5B58\u50A8\u7684\u9AD8\u6027\u80FD\u524D\u9762\u5DF2\u7ECF\u8C08\u8FC7\u4E86\uFF0C\u5F53\u7136\u8FD8\u53EF\u4EE5\u4ECE\u5176\u4ED6\u65B9\u9762\u8FDB\u4E00\u6B65\u4F18\u5316\u6027\u80FD\u3002</p><p>\u6BD4\u5982 Reactor \u7F51\u7EDC IO \u6A21\u578B\u3001\u4E1A\u52A1\u7EBF\u7A0B\u6C60\u7684\u8BBE\u8BA1\u3001\u751F\u4EA7\u7AEF\u7684\u6279\u91CF\u53D1\u9001\u3001Broker \u7AEF\u7684\u5F02\u6B65\u5237\u76D8\u3001\u6D88\u8D39\u7AEF\u7684\u6279\u91CF\u62C9\u53D6\u7B49\u7B49\u3002</p><p><strong>\u5C0F\u7ED3</strong></p><p>\u518D\u603B\u7ED3\u4E0B\uFF0C\u8981\u56DE\u7B54\u597D\uFF1A\u5982\u4F55\u8BBE\u8BA1\u4E00\u4E2A MQ\uFF1F</p><p>1\u3001\u9700\u8981\u4ECE\u529F\u80FD\u6027\u9700\u6C42\uFF08\u6536\u53D1\u6D88\u606F\uFF09\u548C\u975E\u529F\u80FD\u6027\u9700\u6C42\uFF08\u9AD8\u6027\u80FD\u3001\u9AD8\u53EF\u7528\u3001\u9AD8\u6269\u5C55\u7B49\uFF09\u4E24\u65B9\u9762\u5165\u624B\u3002</p><p>2\u3001\u529F\u80FD\u6027\u9700\u6C42\u4E0D\u662F\u91CD\u70B9\uFF0C\u80FD\u8986\u76D6 MQ \u6700\u57FA\u7840\u7684\u529F\u80FD\u5373\u53EF\uFF0C\u81F3\u4E8E\u5EF6\u65F6\u6D88\u606F\u3001\u4E8B\u52A1\u6D88\u606F\u3001\u91CD\u8BD5\u961F\u5217\u7B49\u9AD8\u7EA7\u7279\u6027\u53EA\u662F\u9526\u4E0A\u6DFB\u82B1\u7684\u4E1C\u897F\u3002</p><p>3\u3001\u6700\u6838\u5FC3\u7684\u662F\uFF1A\u80FD\u7ED3\u5408\u529F\u80FD\u6027\u9700\u6C42\uFF0C\u7406\u6E05\u695A\u6574\u4F53\u7684\u6570\u636E\u6D41\uFF0C\u7136\u540E\u987A\u7740\u8FD9\u4E2A\u601D\u8DEF\u53BB\u8003\u8651\u975E\u529F\u80FD\u6027\u7684\u8BC9\u6C42\u5982\u4F55\u6EE1\u8DB3\uFF0C\u8FD9\u624D\u662F\u6280\u672F\u96BE\u70B9\u6240\u5728\u3002</p><h1 id="\u5E38\u89C1\u961F\u5217" tabindex="-1"><a class="header-anchor" href="#\u5E38\u89C1\u961F\u5217" aria-hidden="true">#</a> \u5E38\u89C1\u961F\u5217</h1><ul><li><p>RabbitMQ</p></li><li><p>ActiveMQ</p></li><li><p>RockMQ</p></li><li><p>Kafka</p></li><li><p>ZeroMQ</p></li><li><p>Disque</p></li></ul>',84),h=[t];function o(d,n){return a(),e("div",null,h)}const c=p(i,[["render",o],["__file","MQ_BASE.html.vue"]]);export{c as default};
